@charset "UTF-8";
/****** RESET CSS *******/
html{line-height:1.15;-webkit-text-size-adjust:100%}
body{margin:0}
main{display:block}
h1{font-size:2em;margin:.67em 0}
hr{box-sizing:content-box;height:0;overflow:visible}
pre{font-family:monospace,monospace;font-size:1em}
a{background-color:transparent}
abbr[title]{border-bottom:none;text-decoration:underline;text-decoration:underline dotted}
b,strong{font-weight:bolder}
code,kbd,samp{font-family:monospace,monospace;font-size:1em}
small{font-size:80%}
sub,sup{font-size:75%;line-height:0;position:relative;vertical-align:baseline}
sub{bottom:-.25em}
sup{top:-.5em}
img{border-style:none}
button,input,optgroup,select,textarea{font-family:inherit;font-size:100%;line-height:1.15;margin:0}
button,input{overflow:visible}
button,select{text-transform:none}
button,[type="button"],[type="reset"],[type="submit"]{-webkit-appearance:button}
button::-moz-focus-inner,[type="button"]::-moz-focus-inner,[type="reset"]::-moz-focus-inner,[type="submit"]::-moz-focus-inner{border-style:none;padding:0}
button:-moz-focusring,[type="button"]:-moz-focusring,[type="reset"]:-moz-focusring,[type="submit"]:-moz-focusring{outline:1px dotted ButtonText}
fieldset{padding:.35em .75em .625em}
legend{box-sizing:border-box;color:inherit;display:table;max-width:100%;padding:0;white-space:normal}
progress{vertical-align:baseline}
textarea{overflow:auto}
[type="checkbox"],[type="radio"]{box-sizing:border-box;padding:0}
[type="number"]::-webkit-inner-spin-button,[type="number"]::-webkit-outer-spin-button{height:auto}
[type="search"]{-webkit-appearance:textfield;outline-offset:-2px}
[type="search"]::-webkit-search-decoration{-webkit-appearance:none}
::-webkit-file-upload-button{-webkit-appearance:button;font:inherit}
details{display:block}
summary{display:list-item}
template{display:none}
[hidden]{display:none}
*,
*::before,
*::after{box-sizing:border-box;}
a{text-decoration:none; color:inherit; cursor:pointer;}
button{background-color:transparent; color:inherit; border-width:0; padding:0; cursor:pointer;}
figure{margin:0;}
input::-moz-focus-inner {border:0; padding:0; margin:0;}
ul, ol, dd{margin:0; padding:0; list-style:none;}
h1, h2, h3, h4, h5, h6{margin:0; font-size:inherit; font-weight:inherit;}
p{margin:0;}
cite {font-style:normal;}
fieldset{border-width:0; padding:0; margin:0;}

/* Common */
html {
	font-size: 62.5%;
}
body {
  font-family:"游ゴシック Medium",YuGothic,YuGothicM,"Hiragino Kaku Gothic ProN","Hiragino Kaku Gothic Pro",メイリオ,Meiryo,sans-serif;
  color: #2D2D2D;
	font-size: 1.8rem;
	line-height: 1.8;
}
/* img {
	vertical-align: top;
  width: 100%;
  object-fit: contain;
} */
a:hover {
  cursor: pointer;
  opacity: 0.8;
}

/* Contents */
.simple_scroll {
  background: #FAFAFA;
  padding-bottom: 200px;
  display: flex;
  background-image: url(https://github.com/akitoimase/kintone-portal-designer/blob/main/images/graph_bg.png?raw=true);
  background-repeat: no-repeat;
  background-position: right top 88px;
  background-attachment: fixed;
  background-size: 12%;
}
.simple_scroll.dash_bg {
  background-image: url(https://github.com/akitoimase/kintone-portal-designer/blob/main/images/graph_bg.png?raw=true);
}
.simple_scroll.app_bg {
  background-image: url(https://github.com/akitoimase/kintone-portal-designer/blob/main/images/app_bg.png?raw=true);
}
.simple_scroll.space_bg {
  background-image: url(https://github.com/akitoimase/kintone-portal-designer/blob/main/images/space_bg.png?raw=true);
}
.simple_scroll.link_bg {
  background-image: url(https://github.com/akitoimase/kintone-portal-designer/blob/main/images/link_bg.png?raw=true);
}
.main_column {
  width: calc((100vw - 240px) * 0.8);
  min-width: 720px;
  max-width: 1200px;
  margin-left: max(72px, ((100vw - min(max(720px, (100vw - 240px) * 0.8), 1200px)) / 2) - 240px);
  min-height: calc(100vh - 60px);
}
section {
  max-width: 1152px;
  margin-right: 26px;
  margin-bottom: 12px;
  padding-top: 108px;
}
section:first-of-type {
  margin-bottom: 60px;
  padding-top: 60px;
}
h3 {
  color: #2D2D2D;
  font-weight: bold;
  font-size: 3.2rem;
  margin-bottom: 40px;
}
h4 {
  color: #2D2D2D;
  font-weight: bold;
  font-size: 1.8rem;
  margin-bottom: 12px;
}
h5 {
  color: #2D2D2D;
  font-weight: bold;
  font-size: 1.8rem;
  margin-bottom: 20px;
}

/* Side_menu */
.side_menu {
  width: 10%;
  min-width: 160px;
  margin-left: 26px;
  padding-top: 60px;
}
.side_menu li {
  margin-bottom: 12px;
  font-size: 1.6rem;
}
.side_menu a {
  display: flex;
  align-items: center;
  padding-left: 45px;
}
.side_menu img {
  width: 18px;
  margin-right: 26px;
}
.menu_list a {
  background-repeat: no-repeat;
  background-size: 18px;
  background-position: left center;
}
.dash_li a {
  background-image: url(https://github.com/akitoimase/kintone-portal-designer/blob/main/images/dash_icon.png?raw=true);
}
.dash_li.active a {
  background-image: url(https://github.com/akitoimase/kintone-portal-designer/blob/main/images/dash_icon_hover.png?raw=true);
}
.app_li a {
  background-image: url(https://github.com/akitoimase/kintone-portal-designer/blob/main/images/app_icon.png?raw=true);
}
.app_li.active a {
  background-image: url(https://github.com/akitoimase/kintone-portal-designer/blob/main/images/app_icon_hover.png?raw=true);
}
.space_li a {
  background-image: url(https://github.com/akitoimase/kintone-portal-designer/blob/main/images/space_icon.png?raw=true);
}
.space_li.active a {
  background-image: url(https://github.com/akitoimase/kintone-portal-designer/blob/main/images/space_icon_hover.png?raw=true);
}
.link_li a {
  background-image: url(https://github.com/akitoimase/kintone-portal-designer/blob/main/images/link_icon.png?raw=true);
}
.link_li.active a {
  background-image: url(https://github.com/akitoimase/kintone-portal-designer/blob/main/images/link_icon_hover.png?raw=true);
}
.active {
  font-weight: bold;
}
.js-fix.is-fixed {
  position: fixed;
  top: 108px;
  left: 26px;
}

/* Graph */
.graph_top {
  margin-bottom: 40px;
}
.graph_bottom {
  display: flex;
  justify-content: space-between;
}
.graph_left,
.graph_right {
  width: 48%;
}

/* App */
.app_top {
  margin-bottom: 80px;
}
.app_list {
  background: #fff;
  padding: 46px 110px 40px;
  border-radius: 8px;
  box-shadow: rgb(0 0 0 / 2%) 0px 0px 0px 1px, rgb(0 0 0 / 10%) 0px 1px 3px 0px;
}
.app_list ul {
  display: flex;
  justify-content: space-between;
}
.app_list li {
  width: 11%;
  text-align: center;
}
.app_list li img {
  width: 100%;
}
.app_list li p {
  margin-top: 8px;
  white-space: nowrap;
  font-size: 1.8rem;
}

/* App sp */
.app .sp {
  display: none;
}

/* Space */
.space_list ul {
  display: flex;
  justify-content: space-between;
  flex-wrap: wrap;
}
.space_list li {
  /* width: 32.4%; */
  width: 49%;
  background: #fff;
  border-radius: 8px;
  margin-bottom: 16px;
  box-shadow: rgb(0 0 0 / 2%) 0px 0px 0px 1px, rgb(0 0 0 / 10%) 0px 1px 3px 0px;
}
.space_list li a {
  display: flex;
  align-items: center;
  padding: 20px;
}
.space_list li img {
  width: 14%;
}
.space_list li p {
  line-height: 1.2;
  font-weight: bold;
  /* font-size: 1.8rem; */
  font-size: 2.0rem;
  margin-left: 20px;
}
.space_list li span {
  color: #0000008f;
  /* font-size: 1.4rem; */
  font-size: 1.6rem;
  font-weight: normal;
}

/* Link */
.link_list li {
  background: #fff;
  border-radius: 8px;
  margin-bottom: 16px;
  font-size: 1.8rem;
  font-weight: bold;
  box-shadow: rgb(0 0 0 / 2%) 0px 0px 0px 1px, rgb(0 0 0 / 10%) 0px 1px 3px 0px;
}
.link_list li a {
  padding: 12px 24px;
  display: block;
}
.link_list {
  margin-bottom: 40px;
}


/* Responsive */
@media screen and (max-width: 768px) {
  .side_menu {
    display: none;
  }
  h3 {
    font-size: 2.6rem;
    margin-bottom: 24px;
  }
  h4 {
    font-size: 1.6rem;
  }
  section {
    margin-right: auto;
    margin-bottom: 0;
    padding-top: 60px;
  }
  section:first-of-type {
    margin-bottom: 0;
    padding-top: 40px;
  }
  .main_column {
    width: auto;
    min-width: auto;
    max-width: none;
    margin-left: auto;
    min-height: auto;
    margin: 0 20px;
  }
  .simple_scroll {
    display: block;
    background-size: 40%;
    margin-bottom: 100px;
  }
  .graph_bottom {
    display: block;
  }
  .graph_left, .graph_right {
    width: 100%;
  }
  .graph_left {
    margin-bottom: 40px;
  }
  .space_list ul {
    display: block;
  }
  .space_list li {
    width: 100%;
  }
  .space_list li p {
    font-size: 1.8rem;
  }
  .space_list li span {
    font-size: 1.4rem;
  }
  .app .pc {
    display: none;
  }
  .app .sp {
    display: block;
  }
  .app_sp_list ul {
    display: block;
  }
  .app_sp_list li {
    width: 100%;
    background: #fff;
    border-radius: 8px;
    margin-bottom: 16px;
    box-shadow: rgb(0 0 0 / 2%) 0px 0px 0px 1px, rgb(0 0 0 / 10%) 0px 1px 3px 0px;
  }
  .app_sp_list li a {
    display: flex;
    align-items: center;
    padding: 20px;
  }
  .app_sp_list li img {
    width: 14%;
  }
  .app_sp_list li p {
    line-height: 1.2;
    font-weight: bold;
    font-size: 1.8rem;
    margin-left: 20px;
  }
  .app .sp .app_sp_list:first-of-type {
    margin-bottom: 40px;
  }
}

